INSERT INTO td_equipamento (idt_equipamento, nme_equipamento, dsc_equipamento, tpo_equipamento, mod_base_equipamento) VALUES
 (1,'Manopla', 'Arma simples e de concussão para combate corpo-a-corpo. Ataques com a Manopla são considerados ataques desarmados. Vale 2 PO.', 'A', '1d2 (P) / 1d3 (M)'),
